mapper.select:
    
    SELECT TOP ${pageSize}   ,c.pagemark channelPagemark,c.indexnum channelIndexnum 
    FROM freecms_info i left join freecms_channel c on i.channel=c.id  
    WHERE (i.ID NOT IN  
          (SELECT TOP ${skip} i.id   
         FROM freecms_info i  
	    
	      
	    
         
    
      order by ${orderByClause}
    ))   
         
	    
	       
	    
    
    
      order by ${orderByClause}
